import React, { useContext } from 'react';
import { GeneralContext } from '../context/GeneralContext';


const Login = ({ setIsLogin }) => {
  const { setEmail, setPassword, login } = useContext(GeneralContext);

  const handleLogin = async (e) => {
    e.preventDefault();
    await login();
  };

  return (
    <div className="bg-gray-100 min-h-screen flex items-center justify-center">
      <div className="bg-white shadow-lg rounded-lg w-full max-w-md px-8 py-10">
        <h2 className="text-3xl font-bold mb-6 text-center">Welcome Back!</h2>
        <form className="space-y-6" onSubmit={handleLogin}>
          <div className="relative">
            <FaUserAlt className="absolute top-1/2 transform -translate-y-1/2 left-3 text-gray-400" />
            <input
              type="email"
              className="pl-10 pr-3 py-2 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
              id="email"
              placeholder="Email address"
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="relative">
            <FaLock className="absolute top-1/2 transform -translate-y-1/2 left-3 text-gray-400" />
            <input
              type="password"
              className="pl-10 pr-3 py-2 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring focus:ring-blue-200 focus:ring-opacity-50"
              id="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
          >
            Sign In
          </button>
        </form>
        <div className="mt-6 text-center">
          <p>
            Not registered?{' '}
            <span
              className="text-blue-500 cursor-pointer hover:text-blue-600"
              onClick={() => setIsLogin(false)}
            >
              Register
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
