import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { 
  Users,
  Plane,
  Calendar,
  Check,
  X,
  Mail,
  User
} from 'lucide-react';

const Admin = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [userCount, setUserCount] = useState(0);
  const [bookingCount, setbookingCount] = useState(0);
  const [flightsCount, setFlightsCount] = useState(0);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const usersResponse = await axios.get('http://localhost:6001/fetch-users');
      setUserCount(usersResponse.data.length - 1);
      setUsers(usersResponse.data.filter(user => user.approval === 'not-approved'));

      const bookingsResponse = await axios.get('http://localhost:6001/fetch-bookings');
      setbookingCount(bookingsResponse.data.length);

      const flightsResponse = await axios.get('http://localhost:6001/fetch-flights');
      setFlightsCount(flightsResponse.data.length);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const approveRequest = async (id) => {
    try {
      await axios.post('http://localhost:6001/approve-operator', { id });
      alert("Operator approved!!");
      fetchData();
    } catch (err) {
      console.error('Error approving operator:', err);
    }
  };

  const rejectRequest = async (id) => {
    try {
      await axios.post('http://localhost:6001/reject-operator', { id });
      alert("Operator rejected!!");
      fetchData();
    } catch (err) {
      console.error('Error rejecting operator:', err);
    }
  };

  const stats = [
    {
      title: 'Total Users',
      count: userCount,
      icon: Users,
      action: () => navigate('/all-users'),
      color: 'bg-blue-50',
      iconColor: 'text-blue-600'
    },
    {
      title: 'Total Bookings',
      count: bookingCount,
      icon: Calendar,
      action: () => navigate('/all-bookings'),
      color: 'bg-green-50',
      iconColor: 'text-green-600'
    },
    {
      title: 'Total Flights',
      count: flightsCount,
      icon: Plane,
      action: () => navigate('/all-flights'),
      color: 'bg-purple-50',
      iconColor: 'text-purple-600'
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-gray-800">Admin Dashboard</h1>
        <p className="text-gray-600">Flight Management System</p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        {stats.map((stat, index) => (
          <Card key={index} className="hover:shadow-lg transition-shadow duration-300">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className={`h-12 w-12 ${stat.color} rounded-full flex items-center justify-center`}>
                  <stat.icon className={`h-6 w-6 ${stat.iconColor}`} />
                </div>
                <Button 
                  variant="ghost" 
                  className="text-sm hover:bg-gray-100"
                  onClick={stat.action}
                >
                  View All
                </Button>
              </div>
              <h3 className="text-gray-600 text-sm font-medium">{stat.title}</h3>
              <p className="text-3xl font-bold text-gray-900 mt-2">{stat.count}</p>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Operator Applications */}
      <Card className="mb-8">
        <div className="p-6">
          <div className="flex items-center mb-6">
            <User className="h-5 w-5 text-blue-600 mr-2" />
            <h2 className="text-xl font-semibold text-gray-800">New Operator Applications</h2>
          </div>

          <div className="space-y-4">
            {users.length === 0 ? (
              <div className="text-center py-8 text-gray-500">No new requests...</div>
            ) : (
              users.map((user) => (
                <div 
                  key={user._id} 
                  className="bg-white rounded-lg border border-gray-100 p-4 hover:shadow-md transition-shadow duration-200"
                >
                  <div className="flex items-center justify-between flex-wrap gap-4">
                    <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4">
                      <div className="flex items-center gap-3">
                        <User className="h-5 w-5 text-gray-400" />
                        <div>
                          <p className="font-medium text-gray-900">{user.username}</p>
                          <div className="flex items-center text-gray-500 text-sm">
                            <Mail className="h-4 w-4 mr-1" />
                            {user.email}
                          </div>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex gap-2">
                      <Button 
                        onClick={() => approveRequest(user._id)}
                        className="bg-green-50 hover:bg-green-100 text-green-600"
                      >
                        <Check className="h-4 w-4 mr-1" />
                        Approve
                      </Button>
                      <Button 
                        variant="outline"
                        onClick={() => rejectRequest(user._id)}
                        className="text-red-600 hover:bg-red-50"
                      >
                        <X className="h-4 w-4 mr-1" />
                        Reject
                      </Button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </Card>
    </div>
  );
};

export default Admin;
