import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GeneralContext } from '../context/GeneralContext';
import { Sun, Moon, LogIn } from 'lucide-react';
import axios from 'axios';
import OffersSection from './OffersSection';
import WhyBookSection from './WhyBookSection';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);

  const cities = [
    "Chennai", "Bangalore", "Hyderabad", "Mumbai", "Indore",
    "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata",
    "Varanasi", "Jaipur"
  ];

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
    
    // Check for saved theme preference
    const savedTheme = localStorage.getItem('theme');
    setDarkMode(savedTheme === 'dark');
  }, [navigate]);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    localStorage.setItem('theme', !darkMode ? 'dark' : 'light');
  };

  const fetchFlights = async () => {
    try {
      const date = new Date();
      const date1 = new Date(departureDate);
      const date2 = checkBox ? new Date(returnDate) : null;

      if (!departure || !destination || !departureDate || (checkBox && !returnDate)) {
        setError("Please fill all the required fields");
        return;
      }

      if (date1 < date || (checkBox && date2 < date1)) {
        setError("Please check the dates");
        return;
      }

      setError("");
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
    } catch (err) {
      setError("Failed to fetch flights. Please try again.");
    }
  };

  const handleTicketBooking = async (id, origin, destination) => {
    if (!localStorage.getItem('userId')) {
      navigate('/auth');
      return;
    }

    const bookingDate = origin === departure ? departureDate : returnDate;
    setTicketBookingDate(bookingDate);
    navigate(`/book-flight/${id}`);
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>
      {/* Navigation Bar */}
      <nav className={`fixed w-full z-50 ${darkMode ? 'bg-gray-800' : 'bg-white'} shadow-lg`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16 items-center">
            <div className="flex-shrink-0">
              <h1 className="text-2xl font-bold">iairgo</h1>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={toggleTheme}
                className={`p-2 rounded-full ${darkMode ? 'bg-gray-700 hover:bg-gray-600' : 'bg-gray-100 hover:bg-gray-200'}`}
              >
                {darkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
              </button>
              <button
                onClick={() => navigate('/auth')}
                className={`flex items-center gap-2 px-4 py-2 rounded-full ${
                  darkMode 
                    ? 'bg-blue-600 hover:bg-blue-700' 
                    : 'bg-blue-500 hover:bg-blue-600'
                } text-white transition-colors duration-300`}
              >
                <LogIn className="w-4 h-4" />
                <span>Login</span>
              </button>
            </div>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="pt-16">
        <div className="grid lg:grid-cols-2 min-h-[600px]">
          <div className="flex items-center justify-center p-8 lg:p-16 bg-gradient-to-r from-blue-500 to-blue-600">
            <h1 className="text-4xl lg:text-5xl font-bold text-white max-w-2xl text-center lg:text-left">
              Discover unbeatable fares to your favorite destinations
            </h1>
          </div>

          {/* Search Card */}
          <div className={`p-8 lg:p-12 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
            <div className="max-w-md mx-auto">
              <h2 className="text-2xl font-bold mb-6">Book a flight</h2>
              
              {/* Round Trip Toggle */}
              <label className="relative inline-flex items-center cursor-pointer mb-6">
                <input
                  type="checkbox"
                  checked={checkBox}
                  onChange={(e) => setCheckBox(e.target.checked)}
                  className="sr-only peer"
                />
                <div className={`w-11 h-6 rounded-full peer ${
                  darkMode ? 'bg-gray-700' : 'bg-gray-200'
                } peer-checked:after:translate-x-full after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600`}></div>
                <span className="ml-3">Round Trip</span>
              </label>

              {/* Form Fields */}
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1">From</label>
                  <select
                    value={departure}
                    onChange={(e) => setDeparture(e.target.value)}
                    className={`w-full p-3 rounded-lg border ${
                      darkMode 
                        ? 'bg-gray-700 border-gray-600' 
                        : 'bg-white border-gray-300'
                    }`}
                  >
                    <option value="">Select City</option>
                    {cities.map(city => (
                      <option key={city} value={city}>{city}</option>
                    ))}
                  </select>
                </div>

                {/* Add similar styling for other form fields */}
                {error && (
                  <div className="text-red-500 text-sm mt-2">{error}</div>
                )}

                <button
                  onClick={fetchFlights}
                  className="w-full py-3 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-300"
                >
                  Search Flights
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Flight Results */}
      {flights.length > 0 && (
        <div className={`py-16 ${darkMode ? 'bg-gray-800' : 'bg-gray-50'}`}>
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h2 className="text-3xl font-bold mb-8">Available Flights</h2>
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {flights
                .filter(flight => 
                  checkBox
                    ? (flight.origin === departure && flight.destination === destination) ||
                      (flight.origin === destination && flight.destination === departure)
                    : flight.origin === departure && flight.destination === destination
                )
                .map((flight) => (
                  <div
                    key={flight._id}
                    className={`rounded-xl p-6 ${
                      darkMode 
                        ? 'bg-gray-700 hover:bg-gray-600' 
                        : 'bg-white hover:shadow-lg'
                    } transition-all duration-300`}
                  >
                    <div className="flex justify-between items-center mb-4">
                      <h3 className="text-xl font-bold">{flight.flightName}</h3>
                      <span className="text-sm opacity-70">#{flight.flightId}</span>
                    </div>
                    
                    <div className="flex items-center justify-between mb-6">
                      <div className="text-center">
                        <p className="font-bold">{flight.origin}</p>
                        <p className="text-sm opacity-70">{flight.departureTime}</p>
                      </div>
                      <div className="flex-1 border-t border-dashed mx-4"></div>
                      <div className="text-center">
                        <p className="font-bold">{flight.destination}</p>
                        <p className="text-sm opacity-70">{flight.arrivalTime}</p>
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4 mb-6">
                      <div className="text-center">
                        <p className="text-2xl font-bold">â‚¹{flight.basePrice}</p>
                        <p className="text-sm opacity-70">Starting from</p>
                      </div>
                      <div className="text-center">
                        <p className="text-2xl font-bold">{flight.totalSeats}</p>
                        <p className="text-sm opacity-70">Available Seats</p>
                      </div>
                    </div>

                    <button
                      onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}
                      className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-300"
                    >
                      Book Now
                    </button>
                  </div>
                ))}
            </div>
          </div>
        </div>
      )}

      {/* Other Sections */}
      <OffersSection />
      <WhyBookSection />
    </div>
  );
};

export default LandingPage;
