import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Admin.css';

const Admin = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [userCount, setUserCount] = useState(0);
  const [bookingCount, setbookingCount] = useState(0);
  const [flightsCount, setFlightsCount] = useState(0);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const usersResponse = await axios.get('http://localhost:6001/fetch-users');
      setUserCount(usersResponse.data.length - 1);
      setUsers(usersResponse.data.filter(user => user.approval === 'not-approved'));

      const bookingsResponse = await axios.get('http://localhost:6001/fetch-bookings');
      setbookingCount(bookingsResponse.data.length);

      const flightsResponse = await axios.get('http://localhost:6001/fetch-flights');
      setFlightsCount(flightsResponse.data.length);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const approveRequest = async (id) => {
    try {
      await axios.post('http://localhost:6001/approve-operator', { id });
      alert("Operator approved!!");
      fetchData();
    } catch (err) {
      console.error('Error approving operator:', err);
    }
  };

  const rejectRequest = async (id) => {
    try {
      await axios.post('http://localhost:6001/reject-operator', { id });
      alert("Operator rejected!!");
      fetchData();
    } catch (err) {
      console.error('Error rejecting operator:', err);
    }
  };

  return (
    <div className="admin-page">
      {/* Header */}
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
        <p>Flight Management System</p>
      </div>

      {/* Stats Cards */}
      <div className="admin-page-cards">
        <div className="admin-card users-card">
          <div className="card-icon">👥</div>
          <h4>Total Users</h4>
          <p>{userCount}</p>
          <button onClick={() => navigate('/all-users')}>View All</button>
        </div>

        <div className="admin-card transactions-card">
          <div className="card-icon">✈️</div>
          <h4>Total Bookings</h4>
          <p>{bookingCount}</p>
          <button onClick={() => navigate('/all-bookings')}>View All</button>
        </div>

        <div className="admin-card deposits-card">
          <div className="card-icon">🛫</div>
          <h4>Total Flights</h4>
          <p>{flightsCount}</p>
          <button onClick={() => navigate('/all-flights')}>View All</button>
        </div>
      </div>

      {/* Operator Applications */}
      <div className="admin-requests-container">
        <h3>New Operator Applications</h3>

        <div className="admin-requests">
          {users.length === 0 ? (
            <p className="no-requests">No new requests...</p>
          ) : (
            users.map((user) => (
              <div className="admin-request" key={user._id}>
                <div className="request-info">
                  <span>
                    <b>Operator name:</b> 
                    {user.username}
                  </span>
                  <span>
                    <b>Operator email:</b> 
                    {user.email}
                  </span>
                </div>
                <div className="admin-request-actions">
                  <button 
                    className="btn-primary"
                    onClick={() => approveRequest(user._id)}
                  >
                    Approve
                  </button>
                  <button 
                    className="btn-danger"
                    onClick={() => rejectRequest(user._id)}
                  >
                    Reject
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Admin;
