import React, { useState } from "react";
import "../styles/SeatBooking.css";

const SeatBooking = () => {
  const rows = 10; // Number of rows
  const cols = 6; // Seats per row
  const [selectedSeats, setSelectedSeats] = useState([]);

  const toggleSeatSelection = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const renderSeat = (row, col) => {
    const seatId = `${row}${col}`;
    const isSelected = selectedSeats.includes(seatId);
    const isBooked = false; // Add logic to mark booked seats if needed

    return (
      <div
        key={seatId}
        className={`seat ${isBooked ? "booked" : isSelected ? "selected" : "available"}`}
        onClick={() => !isBooked && toggleSeatSelection(seatId)}
      >
        {seatId}
      </div>
    );
  };

  return (
    <div className="plane">
      {[...Array(rows)].map((_, rowIndex) => (
        <div key={rowIndex} className="row">
          {[...Array(cols)].map((_, colIndex) => renderSeat(rowIndex + 1, colIndex + 1))}
        </div>
      ))}
      <div className="summary">
        <h3>Selected Seats:</h3>
        <p>{selectedSeats.join(", ") || "None"}</p>
      </div>
    </div>
  );
};

export default SeatBooking;
