import React, { useContext, useState } from 'react'
import '../styles/Navbar.css';
import { useNavigate } from 'react-router-dom';
import { GeneralContext } from '../context/GeneralContext';

const Navbar = () => {
    const navigate = useNavigate();
    const usertype = localStorage.getItem('userType');
    const { logout } = useContext(GeneralContext);
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    // Determine navbar class based on usertype
    const navbarClass = `navbar ${usertype === 'admin' ? 'admin' : usertype === 'flight-operator' ? 'operator' : ''}`;

    return (
        <div className={navbarClass}>
            <h3>
                {!usertype ? 'iairgo' :
                 usertype === 'admin' ? 'iairgo (Admin)' :
                 usertype === 'flight-operator' ? 'iairgo (Operator)' :
                 'iargo'}
            </h3>

            {/* Mobile Menu Button */}
            <button className="menu-button" onClick={toggleMenu}>
                <svg className="menu-icon" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                    {isMenuOpen ? (
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    ) : (
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                    )}
                </svg>
            </button>

            <div className={`nav-options ${isMenuOpen ? 'active' : ''}`}>
                {!usertype ? (
                    <>
                        <p onClick={() => navigate('/')}>Home</p>
                        <p onClick={() => navigate('/auth')}>Login</p>
                    </>
                ) : usertype === 'customer' ? (
                    <>
                        <p onClick={() => navigate('/')}>Home</p>
                        <p onClick={() => navigate('/bookings')}>Bookings</p>
                        <p onClick={logout}>Logout</p>
                    </>
                ) : usertype === 'admin' ? (
                    <>
                        <p onClick={() => navigate('/admin')}>Home</p>
                        <p onClick={() => navigate('/all-users')}>Users</p>
                        <p onClick={() => navigate('/all-bookings')}>Bookings</p>
                        <p onClick={() => navigate('/all-flights')}>Flights</p>
                        <p onClick={logout}>Logout</p>
                    </>
                ) : usertype === 'flight-operator' ? (
                    <>
                        <p onClick={() => navigate('/flight-admin')}>Home</p>
                        <p onClick={() => navigate('/flight-bookings')}>Bookings</p>
                        <p onClick={() => navigate('/flights')}>Flights</p>
                        <p onClick={() => navigate('/new-flight')}>Add Flight</p>
                        <p onClick={logout}>Logout</p>
                    </>
                ) : null}
            </div>
        </div>
    );
};

export default Navbar;
