import React, { useContext, useEffect, useState } from 'react';
import '../styles/BookFlight.css';
import { GeneralContext } from '../context/GeneralContext';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const BookFlight = () => {
    const { id } = useParams();
    const { ticketBookingDate } = useContext(GeneralContext);
    const navigate = useNavigate();

    const [flightName, setFlightName] = useState('');
    const [flightId, setFlightId] = useState('');
    const [basePrice, setBasePrice] = useState(0);
    const [startCity, setStartCity] = useState('');
    const [destinationCity, setDestinationCity] = useState('');
    const [startTime, setStartTime] = useState('');

    const [email, setEmail] = useState('');
    const [mobile, setMobile] = useState('');
    const [coachType, setCoachType] = useState('');
    const [journeyDate, setJourneyDate] = useState(ticketBookingDate);
    const [numberOfPassengers, setNumberOfPassengers] = useState(0);
    const [passengerDetails, setPassengerDetails] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [errors, setErrors] = useState({});
    const [successMessage, setSuccessMessage] = useState('');

    const price = { 'economy': 1, 'premium-economy': 2, 'business': 3, 'first-class': 4 };

    useEffect(() => {
        fetchFlightData();
    }, []);

    useEffect(() => {
        if (price[coachType] * basePrice * numberOfPassengers) {
            setTotalPrice(price[coachType] * basePrice * numberOfPassengers);
        }
    }, [numberOfPassengers, coachType]);

    const fetchFlightData = async () => {
        try {
            const response = await axios.get(`http://localhost:6001/fetch-flight/${id}`);
            const data = response.data;
            setFlightName(data.flightName);
            setFlightId(data.flightId);
            setBasePrice(data.basePrice);
            setStartCity(data.origin);
            setDestinationCity(data.destination);
            setStartTime(data.departureTime);
        } catch (error) {
            console.error('Error fetching flight data:', error);
        }
    };

    const handlePassengerChange = (event) => {
        const value = parseInt(event.target.value, 10);
        setNumberOfPassengers(value);
        setPassengerDetails(Array(value).fill({ name: '', age: '' }));
    };

    const handlePassengerDetailsChange = (index, key, value) => {
        setPassengerDetails((prevDetails) => {
            const updatedDetails = [...prevDetails];
            updatedDetails[index] = { ...updatedDetails[index], [key]: value };
            return updatedDetails;
        });
    };

    const validateForm = () => {
        const newErrors = {};
        if (!email) newErrors.email = 'Email is required';
        if (!mobile) newErrors.mobile = 'Mobile number is required';
        if (!coachType) newErrors.coachType = 'Please select a seat class';
        if (numberOfPassengers <= 0) newErrors.numberOfPassengers = 'Number of passengers must be greater than zero';
        if (!journeyDate) newErrors.journeyDate = 'Journey date is required';
        passengerDetails.forEach((passenger, index) => {
            if (!passenger.name) newErrors[`passenger${index}Name`] = `Passenger ${index + 1} name is required`;
            if (!passenger.age) newErrors[`passenger${index}Age`] = `Passenger ${index + 1} age is required`;
        });
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const bookFlight = async () => {
        if (!validateForm()) return;

        const inputs = {
            user: localStorage.getItem('userId'),
            flight: id,
            flightName,
            flightId,
            departure: startCity,
            journeyTime: startTime,
            destination: destinationCity,
            email,
            mobile,
            passengers: passengerDetails,
            totalPrice,
            journeyDate,
            seatClass: coachType,
        };

        try {
            await axios.post('http://localhost:6001/book-ticket', inputs);
            setSuccessMessage('Booking successful!');
            setTimeout(() => {
                navigate('/bookings');
            }, 2000);
        } catch (error) {
            alert('Booking failed! Please try again.');
        }
    };

    return (
        <div className='BookFlightPage'>
            <div className="BookingFlightPageContainer">
                <h2>Book Ticket</h2>
                <div className="flight-info">
                    <p><b>Flight Name:</b> {flightName}</p>
                    <p><b>Flight No:</b> {flightId}</p>
                    <p><b>Base Price:</b> ₹{basePrice}</p>
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div className="form-group">
                    <label>Mobile</label>
                    <input type="text" value={mobile} onChange={(e) => setMobile(e.target.value)} />
                    {errors.mobile && <span className="error">{errors.mobile}</span>}
                </div>
                <div className="form-group">
                    <label>Number of Passengers</label>
                    <input type="number" value={numberOfPassengers} onChange={handlePassengerChange} />
                    {errors.numberOfPassengers && <span className="error">{errors.numberOfPassengers}</span>}
                </div>
                <div className="form-group">
                    <label>Journey Date</label>
                    <input type="date" value={journeyDate} onChange={(e) => setJourneyDate(e.target.value)} />
                    {errors.journeyDate && <span className="error">{errors.journeyDate}</span>}
                </div>
                <div className="form-group">
                    <label>Seat Class</label>
                    <select value={coachType} onChange={(e) => setCoachType(e.target.value)}>
                        <option value="" disabled>Select</option>
                        <option value="economy">Economy</option>
                        <option value="premium-economy">Premium Economy</option>
                        <option value="business">Business</option>
                        <option value="first-class">First Class</option>
                    </select>
                    {errors.coachType && <span className="error">{errors.coachType}</span>}
                </div>
                <div className="passenger-details">
                    {Array.from({ length: numberOfPassengers }).map((_, index) => (
                        <div key={index} className="passenger-input">
                            <h4>Passenger {index + 1}</h4>
                            <input
                                type="text"
                                placeholder="Name"
                                value={passengerDetails[index]?.name || ''}
                                onChange={(e) => handlePassengerDetailsChange(index, 'name', e.target.value)}
                            />
                            {errors[`passenger${index}Name`] && <span className="error">{errors[`passenger${index}Name`]}</span>}
                            <input
                                type="number"
                                placeholder="Age"
                                value={passengerDetails[index]?.age || ''}
                                onChange={(e) => handlePassengerDetailsChange(index, 'age', e.target.value)}
                            />
                            {errors[`passenger${index}Age`] && <span className="error">{errors[`passenger${index}Age`]}</span>}
                        </div>
                    ))}
                </div>
                <h4>Total Price: ₹{totalPrice}</h4>
                <button className="btn btn-primary" onClick={bookFlight}>Book Now</button>
                {successMessage && <div className="success">{successMessage}</div>}
            </div>
        </div>
    );
};

export default BookFlight;
