import React, { useContext, useState } from 'react';
import { GeneralContext } from '../context/GeneralContext';

const Register = ({ setIsLogin }) => {
  const {
    setUsername,
    setEmail,
    setPassword,
    setUsertype,
    register,
  } = useContext(GeneralContext);

  const [username, localSetUsername] = useState('');
  const [email, localSetEmail] = useState('');
  const [password, localSetPassword] = useState('');
  const [usertype, localSetUsertype] = useState('');
  const [errors, setErrors] = useState({});

  const handleRegister = async (e) => {
    e.preventDefault();

    const newErrors = {};
    if (username.trim().length < 3) {
      newErrors.username = 'Username must be at least 3 characters.';
    }
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = 'Invalid email format.';
    }
    if (password.trim().length < 6) {
      newErrors.password = 'Password must be at least 6 characters.';
    }
    if (!usertype) {
      newErrors.usertype = 'Please select a user type.';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return; // Stop execution if there are validation errors
    }

    // Update global state
    setUsername(username);
    setEmail(email);
    setPassword(password);
    setUsertype(usertype);

    // Call register function
    try {
      await register(); // Call register only if all validations pass
      alert("Registration successful!");
      setIsLogin(true); // Redirect to login page
    } catch (error) {
      console.error("Registration failed:", error);
      alert("Registration failed. Please try again.");
    }
  };

  return (
    <form className="authForm" onSubmit={handleRegister}>
      <h2>Register</h2>
      <div className="form-floating mb-3 authFormInputs">
        <input
          type="text"
          className="form-control"
          id="floatingInput"
          placeholder="username"
          value={username}
          onChange={(e) => {
            localSetUsername(e.target.value);
            setErrors({ ...errors, username: '' });
          }}
        />
        <label htmlFor="floatingInput">Username</label>
        {errors.username && <p style={{ color: 'red' }}>{errors.username}</p>}
      </div>
      <div className="form-floating mb-3 authFormInputs">
        <input
          type="email"
          className="form-control"
          id="floatingEmail"
          placeholder="name@example.com"
          value={email}
          onChange={(e) => {
            localSetEmail(e.target.value);
            setErrors({ ...errors, email: '' });
          }}
        />
        <label htmlFor="floatingEmail">Email address</label>
        {errors.email && <p style={{ color: 'red' }}>{errors.email}</p>}
      </div>
      <div className="form-floating mb-3 authFormInputs">
        <input
          type="password"
          className="form-control"
          id="floatingPassword"
          placeholder="Password"
          value={password}
          onChange={(e) => {
            localSetPassword(e.target.value);
            setErrors({ ...errors, password: '' });
          }}
        />
        <label htmlFor="floatingPassword">Password</label>
        {errors.password && <p style={{ color: 'red' }}>{errors.password}</p>}
      </div>
      <select
        className="form-select form-select-lg mb-3"
        value={usertype}
        onChange={(e) => {
          localSetUsertype(e.target.value);
          setErrors({ ...errors, usertype: '' });
        }}
      >
        <option value="">User type</option>
        <option value="admin">Admin</option>
        <option value="customer">Customer</option>
        <option value="flight-operator">Flight Operator</option>
      </select>
      {errors.usertype && <p style={{ color: 'red' }}>{errors.usertype}</p>}
      <button className="btn btn-primary">Sign up</button>
      <p>
        Already registered? <span onClick={() => setIsLogin(true)}>Login</span>
      </p>
    </form>
  );
};

export default Register;
