import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GeneralContext } from '../context/GeneralContext';
import { Plane, Calendar, ArrowRight, ArrowLeftRight } from 'lucide-react';
import axios from 'axios';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);

  const cities = [
    "Chennai", "Bangalore", "Hyderabad", "Mumbai", "Indore",
    "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata",
    "Varanasi", "Jaipur"
  ];

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, [navigate]);

  const fetchFlights = async () => {
    try {
      const date = new Date();
      const date1 = new Date(departureDate);
      const date2 = checkBox ? new Date(returnDate) : null;

      if (!departure || !destination || !departureDate || (checkBox && !returnDate)) {
        setError("Please fill all the required fields");
        return;
      }

      if (date1 < date || (checkBox && date2 < date1)) {
        setError("Please check the dates");
        return;
      }

      setError("");
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
    } catch (err) {
      setError("Failed to fetch flights. Please try again.");
    }
  };

  const handleTicketBooking = async (id, origin, destination) => {
    if (!localStorage.getItem('userId')) {
      navigate('/auth');
      return;
    }

    const bookingDate = origin === departure ? departureDate : returnDate;
    setTicketBookingDate(bookingDate);
    navigate(`/book-flight/${id}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white">
      {/* Hero Section */}
      <div className="relative overflow-hidden bg-blue-600 text-white">
        <div className="absolute inset-0 bg-[url('/api/placeholder/1920/1080')] opacity-20 bg-cover bg-center" />
        <div className="container mx-auto px-4 py-16 relative">
          <div className="max-w-3xl mx-auto text-center">
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Embark on an Extraordinary Flight Journey
            </h1>
            <p className="text-xl opacity-90 mb-8">
              Discover seamless travel experiences and unforgettable destinations
            </p>
          </div>

          {/* Search Card */}
          <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-2xl p-6 mt-8">
            <div className="flex items-center gap-2 mb-6">
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="returnTrip"
                  checked={checkBox}
                  onChange={(e) => setCheckBox(e.target.checked)}
                  className="w-4 h-4 text-blue-600"
                />
                <label htmlFor="returnTrip" className="text-gray-700">Round Trip</label>
              </div>
              <ArrowLeftRight className="h-4 w-4 text-gray-500" />
            </div>

            <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="space-y-1">
                <label className="text-sm font-medium text-gray-700">From</label>
                <select
                  value={departure}
                  onChange={(e) => setDeparture(e.target.value)}
                  className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select City</option>
                  {cities.map(city => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
              </div>

              <div className="space-y-1">
                <label className="text-sm font-medium text-gray-700">To</label>
                <select
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                  className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select City</option>
                  {cities.map(city => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
              </div>

              <div className="space-y-1">
                <label className="text-sm font-medium text-gray-700">Departure</label>
                <input
                  type="date"
                  value={departureDate}
                  onChange={(e) => setDepartureDate(e.target.value)}
                  className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                />
              </div>

              {checkBox && (
                <div className="space-y-1">
                  <label className="text-sm font-medium text-gray-700">Return</label>
                  <input
                    type="date"
                    value={returnDate}
                    onChange={(e) => setReturnDate(e.target.value)}
                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              )}
            </div>

            {error && (
              <p className="text-red-500 mt-2 text-sm">{error}</p>
            )}

            <button
              onClick={fetchFlights}
              className="w-full mt-6 bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
            >
              <Plane className="h-5 w-5" />
              Search Flights
            </button>
          </div>
        </div>
      </div>

      {/* Flight Results */}
      {flights.length > 0 && (
        <div className="container mx-auto px-4 py-12">
          <h2 className="text-2xl font-bold mb-6">Available Flights</h2>
          <div className="grid gap-6">
            {flights
              .filter(flight => 
                checkBox
                  ? (flight.origin === departure && flight.destination === destination) ||
                    (flight.origin === destination && flight.destination === departure)
                  : flight.origin === departure && flight.destination === destination
              )
              .map((flight) => (
                <div
                  key={flight._id}
                  className="bg-white rounded-lg shadow-lg p-6 transition-transform hover:scale-[1.02]"
                >
                  <div className="grid md:grid-cols-5 gap-4 items-center">
                    <div>
                      <h3 className="font-bold text-lg">{flight.flightName}</h3>
                      <p className="text-gray-600 text-sm">Flight #{flight.flightId}</p>
                    </div>
                    
                    <div className="flex items-center gap-2">
                      <div>
                        <p className="font-medium">{flight.origin}</p>
                        <p className="text-sm text-gray-600">{flight.departureTime}</p>
                      </div>
                      <ArrowRight className="h-4 w-4 text-gray-400" />
                      <div>
                        <p className="font-medium">{flight.destination}</p>
                        <p className="text-sm text-gray-600">{flight.arrivalTime}</p>
                      </div>
                    </div>

                    <div>
                      <p className="font-medium">â‚¹{flight.basePrice}</p>
                      <p className="text-sm text-gray-600">Starting from</p>
                    </div>

                    <div>
                      <p className="font-medium">{flight.totalSeats}</p>
                      <p className="text-sm text-gray-600">Available Seats</p>
                    </div>

                    <button
                      onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}
                      className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      Book Now
                    </button>
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}

      {/* About Section */}
      <section className="bg-gray-50 py-16">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-8">About Us</h2>
          <div className="max-w-3xl mx-auto space-y-6 text-gray-600">
            <p>
              Welcome to our Flight ticket booking app, where we are dedicated to providing you with an exceptional travel experience from start to finish. Whether you're embarking on a daily commute, planning an exciting cross-country adventure, or seeking a leisurely scenic route, our app offers an extensive selection of Flight options to cater to your unique travel preferences.
            </p>
            <p>
              We understand the importance of convenience and efficiency in your travel plans. Our user-friendly interface allows you to effortlessly browse through a wide range of Flight schedules, compare fares, and choose the most suitable seating options.
            </p>
            <p>
              With our Flight ticket booking app, you can embrace the joy of exploring new destinations, immerse yourself in breathtaking scenery, and create cherished memories along the way.
            </p>
          </div>
          <div className="text-center mt-8 text-gray-500">
            <p>2024 SB FlightConnect - &copy; All rights reserved</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
