import React, { useState } from 'react';

const HeroSection = () => {
  const [showSearchForm, setShowSearchForm] = useState(false);

  const handleBookNowClick = () => {
    setShowSearchForm(true);
  };

  return (
    <div className="hero-section">
      <div className="hero-content">
        <h1>Crypto s*curity made easy</h1>
        <p>Keep your coins safe and secure with Safepark hardware wallets, app, and backup solutions, ensuring reliable storage, management, and protection.</p>
        <button className="book-now-button" onClick={handleBookNowClick}>
          Book Now
        </button>
      </div>
      {showSearchForm && <SearchForm />}
    </div>
  );
};

const SearchForm = () => {
  // Implement the search form component here
  return (
    <div className="search-form">
      {/* Add your search form input fields and functionality */}
    </div>
  );
};

export default HeroSection;
