import React from 'react';
import { useNavigate } from 'react-router-dom';
import { GeneralContext } from '../context/GeneralContext';
import axios from 'axios';
import OffersSection from './OffersSection';
import WhyBookSection from './WhyBookSection';
import { Plane, Calendar, MapPin } from 'lucide-react';

const LandingPageRedesign = () => {
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);

  const cities = [
    "Chennai", "Bangalore", "Hyderabad", "Mumbai", "Indore",
    "Delhi", "Pune", "Trivendrum", "Bhopal", "Kolkata",
    "Varanasi", "Jaipur"
  ].sort(); // Sorted cities alphabetically

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') navigate('/admin');
    else if (userType === 'flight-operator') navigate('/flight-admin');
  }, [navigate]);

  const fetchFlights = async () => {
    // Existing fetchFlights logic
  };

  const handleTicketBooking = async (id, origin, destination) => {
    // Existing handleTicketBooking logic
  };

  const formatTime = (time) => {
    // Existing formatTime logic
  };

  return (
    <div className="landing-page-redesign">
      <div className="hero-section">
        <div className="hero-content">
          <div className="hero-text-container">
            <h1 className="hero-title">
              Discover unbeatable fares on iairgo to your <br />
              favorite destinations with fast and easy booking
            </h1>
            <p className="hero-description">
              Find the best flight deals and book your next trip with confidence.
            </p>
          </div>
        </div>

        <div className="search-card">
          <div className="search-header">
            <h2 className="search-title"><Plane className="inline-icon" /> Book a flight</h2>
            <div className="round-trip-toggle">
              <input
                type="checkbox"
                id="returnTrip"
                checked={checkBox}
                onChange={(e) => setCheckBox(e.target.checked)}
              />
              <label htmlFor="returnTrip">Round Trip</label>
            </div>
          </div>

          <div className="search-form">
            <div className="form-group">
              <label><MapPin className="inline-icon" /> From</label>
              <select value={departure} onChange={(e) => setDeparture(e.target.value)}>
                <option value="">Select City</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label><MapPin className="inline-icon" /> To</label>
              <select value={destination} onChange={(e) => setDestination(e.target.value)}>
                <option value="">Select City</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label><Calendar className="inline-icon" /> Departure</label>
              <input
                type="date"
                value={departureDate}
                onChange={(e) => setDepartureDate(e.target.value)}
                min={new Date().toISOString().split('T')[0]}
              />
            </div>

            {checkBox && (
              <div className="form-group">
                <label><Calendar className="inline-icon" /> Return</label>
                <input
                  type="date"
                  value={returnDate}
                  onChange={(e) => setReturnDate(e.target.value)}
                  min={departureDate || new Date().toISOString().split('T')[0]}
                />
              </div>
            )}

            {error && <p className="error-message">{error}</p>}

            <button 
              onClick={fetchFlights} 
              className={`search-button ${loading ? 'loading' : ''}`}
              disabled={loading}
            >
              {loading ? 'Searching...' : 'Search Flights'}
            </button>
          </div>
        </div>
      </div>

      {flights.length > 0 ? (
        <div className="flights-section">
          <h2 className="flights-title">Available Flights</h2>
          <div className="flights-grid">
            {flights
              .filter(flight => 
                checkBox
                  ? (flight.origin === departure && flight.destination === destination) ||
                    (flight.origin === destination && flight.destination === departure)
                  : flight.origin === departure && flight.destination === destination
              )
              .map((flight) => (
                <div key={flight._id} className="flight-card">
                  <div className="flight-header">
                    <h3 className="flight-name">{flight.flightName}</h3>
                    <span className="flight-number">Flight #{flight.flightId}</span>
                  </div>
                  
                  <div className="flight-route">
                    <div className="route-point">
                      <p className="city">{flight.origin}</p>
                      <p className="time">{formatTime(flight.departureTime)}</p>
                    </div>
                    <div className="route-arrow">→</div>
                    <div className="route-point">
                      <p className="city">{flight.destination}</p>
                      <p className="time">{formatTime(flight.arrivalTime)}</p>
                    </div>
                  </div>

                  <div className="flight-details">
                    <div className="detail-item">
                      <p className="value">₹{flight.basePrice.toLocaleString()}</p>
                      <p className="label">Starting from</p>
                    </div>
                    <div className="detail-item">
                      <p className="value">{flight.totalSeats}</p>
                      <p className="label">Available Seats</p>
                    </div>
                  </div>

                  <button
                    onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}
                    className="book-button"
                  >
                    Book Now
                  </button>
                </div>
              ))}
          </div>
        </div>
      ) : flights.length === 0 && !error && !loading && (
        <div className="no-flights">
          <h3 className="no-flights-title">No Flights Found</h3>
          <p className="no-flights-message">Try adjusting your search criteria</p>
        </div>
      )}
      
      <OffersSection />
      <WhyBookSection/>

      <footer className="footer">
        <div className="footer-content">
          <p className="copyright">&copy; 2023 iairgo. All rights reserved.</p>
          <nav className="footer-nav">
            <a href="#" className="footer-link">About</a>
            <a href="#" className="footer-link">Contact</a>
            <a href="#" className="footer-link">Privacy Policy</a>
            <a href="#" className="footer-link">Terms of Service</a>
          </nav>
        </div>
      </footer>
    </div>
  );
};

export default LandingPageRedesign;
