import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Menu, X } from 'lucide-react';
import { GeneralContext } from '../context/GeneralContext';

const Navbar = () => {
  const navigate = useNavigate();
  const usertype = localStorage.getItem('userType');
  const { logout } = useContext(GeneralContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleNavClick = (path) => {
    navigate(path);
    setIsMenuOpen(false);
  };

  const handleLogout = () => {
    logout();
    setIsMenuOpen(false);
  };

  const getTitleSuffix = () => {
    if (usertype === 'admin') return ' (Admin)';
    if (usertype === 'flight-operator') return ' (Operator)';
    return '';
  };

  const renderNavOptions = () => {
    switch (usertype) {
      case null:
      case undefined:
        return [
          { label: 'Home', action: () => handleNavClick('/') },
          { label: 'Login', action: () => handleNavClick('/auth') }
        ];
      case 'customer':
        return [
          { label: 'Home', action: () => handleNavClick('/') },
          { label: 'Bookings', action: () => handleNavClick('/bookings') },
          { label: 'Logout', action: handleLogout }
        ];
      case 'admin':
        return [
          { label: 'Home', action: () => handleNavClick('/admin') },
          { label: 'Users', action: () => handleNavClick('/all-users') },
          { label: 'Bookings', action: () => handleNavClick('/all-bookings') },
          { label: 'Flights', action: () => handleNavClick('/all-flights') },
          { label: 'Logout', action: handleLogout }
        ];
      case 'flight-operator':
        return [
          { label: 'Home', action: () => handleNavClick('/flight-admin') },
          { label: 'Bookings', action: () => handleNavClick('/flight-bookings') },
          { label: 'Flights', action: () => handleNavClick('/flights') },
          { label: 'Add Flight', action: () => handleNavClick('/new-flight') },
          { label: 'Logout', action: handleLogout }
        ];
      default:
        return [];
    }
  };

  const getNavbarClass = () => {
    const baseClass = 'fixed top-0 w-full h-16 z-50 px-6 flex justify-between items-center transition-all duration-300';
    if (usertype === 'admin') {
      return `${baseClass} bg-gradient-to-r from-blue-900 to-green-900`;
    }
    if (usertype === 'flight-operator') {
      return `${baseClass} bg-gradient-to-r from-blue-900 to-indigo-900`;
    }
    return `${baseClass} bg-gray-900`;
  };

  return (
    <nav className={getNavbarClass()}>
      {/* Logo and Brand Section */}
      <div 
        className="flex items-center gap-2 h-10 cursor-pointer" 
        onClick={() => handleNavClick('/')}
      >
        <img 
          src="/api/placeholder/40/40"  // Replace with your actual logo import
          alt="SB Flights Logo" 
          className="h-full w-auto object-contain"
        />
        <span className="text-white font-semibold text-base md:text-xl">
          SB Flights{getTitleSuffix()}
        </span>
      </div>

      {/* Mobile Menu Button */}
      <button 
        onClick={() => setIsMenuOpen(!isMenuOpen)}
        className="md:hidden p-2 text-white hover:bg-white/10 rounded-lg transition-colors"
      >
        {isMenuOpen ? (
          <X className="w-6 h-6" />
        ) : (
          <Menu className="w-6 h-6" />
        )}
      </button>

      {/* Navigation Options */}
      <div className={`absolute md:relative top-16 md:top-0 left-0 right-0 md:right-auto
        bg-blue-900/95 md:bg-transparent backdrop-blur-md md:backdrop-blur-none
        md:flex items-center gap-8 p-4 md:p-0
        transform transition-all duration-300 ease-in-out
        ${isMenuOpen ? 'translate-y-0 opacity-100 visible' : '-translate-y-full md:translate-y-0 opacity-0 md:opacity-100 invisible md:visible'}
        flex flex-col md:flex-row`}
      >
        {renderNavOptions().map((option, index) => (
          <p
            key={index}
            onClick={option.action}
            className="relative m-0 text-gray-200 hover:text-white cursor-pointer py-1
              w-full md:w-auto text-center
              border-b md:border-b-0 border-white/10 last:border-b-0
              hover:bg-white/10 md:hover:bg-transparent
              transition-colors duration-200
              md:after:content-[''] md:after:absolute md:after:w-0 md:after:h-0.5
              md:after:bottom-0 md:after:left-0 md:after:bg-blue-500
              md:hover:after:w-full md:after:transition-all md:after:duration-300"
          >
            {option.label}
          </p>
        ))}
      </div>
    </nav>
  );
};

export default Navbar;
